    private void loginButtonButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_loginButtonButtonActionPerformed
        String userName, userEmail, userPassword, query, fname = null, dbPassword = null;
        String url, user, pass;
        url = "jdbc:MySQL://localhost:3306/java_login";
        user = "root";
        pass = "";
        int notFound = 0;
        
        try{
            Class.forName("com.mysql.cj.jdbc.Driver");   
            Connection conn = DriverManager.getConnection(url, user, pass);
            Statement statement = conn.createStatement();
            
             if("".equals(email.getText())){
                JOptionPane.showMessageDialog(new JFrame(), "Email Address is require", "Error",
                        JOptionPane.ERROR_MESSAGE);
            }else if("".equals(password.getText())){
                JOptionPane.showMessageDialog(new JFrame(), "Password is require", "Error",
                        JOptionPane.ERROR_MESSAGE);
            }else {
            userEmail    = email.getText();
            userPassword = password.getText();
            
            query = "SELECT * FROM user WHERE email= '"+ userEmail +"'";
       
            ResultSet rs = statement.executeQuery(query);
            while(rs.next()){
                dbPassword = rs.getString("password");
                fname = rs.getString("name");
                notFound = 1;
            }
            if(notFound == 1 && userPassword.equals(dbPassword)){
                Home HomeFrame = new Home();
                HomeFrame.setUser(fname);
                HomeFrame.setVisible(true);
                HomeFrame.pack();
                HomeFrame.setLocationRelativeTo(null); 
                this.dispose();
                System.out.println("OK!");
            }else{
               JOptionPane.showMessageDialog(new JFrame(), "Incorrect email or password", "Error",
                        JOptionPane.ERROR_MESSAGE);
            }
            password.setText("");
            
            }   
        }
        catch(HeadlessException | ClassNotFoundException | SQLException e){
            System.out.println("Error : " + e.getMessage());
        }
    }//GEN-LAST:event_loginButtonButtonActionPerformed

